import java.util.ArrayList;

public class No07_8 {
    public static void main(String[] args) {
        String input = "";

        int index = 0;
        ArrayList<Long> sumList = new ArrayList<Long>();
        while (index < input.length() - 13) {
            ArrayList<Integer> subsetList = new ArrayList<Integer>();
            String subset = input.substring(index, index + 13);
            if (subset.contains("0")) {
                index++;
                continue;
            }
            for (int i = 0; i < subset.length(); i++)
                subsetList.add(Integer.parseInt((subset.substring(i, i + 1))));

            long sum = 1;
            for (var x : subsetList)
                sum *= x;
            sumList.add(sum);
            index++;
        }
        long max = 0;
        for (var x : sumList)
            if (x > max)
                max = x;
        System.out.print(max);
    }
}
